/* eslint-disable no-unused-vars */
import React from 'react'
import axios from 'axios'
import { useEffect,useState } from 'react'

// import { response } from 'express'

function Wordlist() {
 const [words,setWords] = useState([])
 
    useEffect(()=>{
       
    axios
    .get("http://localhost:2000/api/words/",{ params: { bookname: "book1" } })
    .then((res)=>{
        setWords(res.data)
        
  
     })
   
 },[])
 function card(word){
    return (
    <div>
    <h1>{word.word}</h1>
    <h2>{word.meaning}</h2>
    </div>
    )
          
 }

  return (
    
    <div>
    <h1>Words</h1>
    <div className='booklist'>
    {words.map(card)}
  
  
    
    </div>
    </div>
  )
}

export default Wordlist
